import {useState} from 'react'
import React from 'react'
import { Button, Icon} from 'semantic-ui-react'

const  ItemCount = ({ stock, initial,  onAdd }) => {

    const [counter, setCounter]= useState(initial)

    let sumar = () =>{
        if (counter<stock){
            setCounter(counter+1)
        }
        else alert('No hay mas en Stock')
    }
    let restar = ()=> {
        if(counter>initial){
            setCounter(counter-1)
        }
        else alert('No puede ser negativo')

    }
    return (
        <>
        <div>
            <p>cantidad :{counter}</p>
            <Icon onClick={restar} color ='red' name='minus' size='large'/>
            <Icon onClick={sumar} color ='green' name='plus' size='large'/>
            </div>
            <Button basic color='blue'>agregar al carritor</Button>
        </>
    )
}

export default  ItemCount
